  const subtaskId = subtaskResults[i].json.id;
  const originalData = originalStructure[i].json;

  if (originalData.subtasks) {
    originalData.subtasks.forEach(subSubtask => {
      subSubtasks.push({
        name: subSubtask.name,
        assignees: subSubtask.assignees,
        priority: subSubtask.priority,
        parent: subtaskId
      });
    });
