// Step 1: Create all main subtasks first
const structure = $input.first().json.structure;
const mainSubtasks = structure.map(item => ({
  type: 'main-subtask',
  name: item.name,
  priority: item.priority,
  assignees: item.assignees,
  time_estimate: item.time_estimate,
  parent: item.parent,
  content: item.content || '',
  subItems: item.subItems
}));

return mainSubtasks.map(task => ({ json: task }));

// Step 2: Create all sub-subtasks using the created main subtask IDs
const mainSubtaskResults = $('Create Main Subtasks').all();
const originalStructure = $('Prepare Main Subtasks').all();

const allSubSubtasks = [];

for (let i = 0; i < mainSubtaskResults.length; i++) {
  const mainSubtaskId = mainSubtaskResults[i].json.id;
  const originalData = originalStructure[i].json;

  if (originalData.subItems && originalData.subItems.length > 0) {
    originalData.subItems.forEach(subItem => {
      allSubSubtasks.push({
        name: subItem.name,
        assignees: subItem.assignees,
        priority: subItem.priority,
        parent: mainSubtaskId,
        content: subItem.content || ''
      });
    });
  }
}

return allSubSubtasks.map(task => ({ json: task }));
